import { useState } from 'react'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const onClick = (props) =>{ 
   if(props === 'good'){
    return setGood(good + 1)
   } else if(props==='neutral'){
   return  setNeutral(neutral + 1)
   } else if (props === 'bad' ){return setBad(bad + 1)}

  }
 const [sum, setSum] = useState(good+bad+neutral)
 const average=()=>{
  setSum(good+neutral+bad)
 return  sum/3
 }
 const percentage=()=>{
  return good / sum * 100
 }
  return (
    <div>
      <h1>Give Feedback</h1>
      <button onClick={()=> onClick('good')}>Good</button>
      <button onClick ={()=> onClick('neutral')}>Neutral</button>
      <button onClick ={()=>onClick('bad')}>Bad</button>
      <h1>Statistics</h1>
      <p>Good {good}</p>
      <p>Neutral {neutral}</p>
      <p>Bad {bad}</p>
      <p>Sum {sum}</p>
      <p>Average {average()}</p>
      <p>Positive {percentage()}</p>
    </div>
  )
}

export default App
