import React from 'react';

const Course = ({ course }) => {
    const totalExercises = course.parts.reduce((sum, part) => sum + part.exercises, 0);
  return (
    <div>
      <h1>{course.name}</h1>
      {course.parts.map(part => (
        <div key={part.id}>
          <p>{part.name} {part.exercises} </p>
        </div>
      ))}
     <div>total of {totalExercises}</div>
    </div>
  );
};

export default Course;
